  (doc) => doc?.['id'] === autenticado
);

const usuarioRole = usuario?.['role'];

// Apenas se estiver na tela "Home" e o usuário for admin
if (telaAtual === 'Home' && usuarioRole === 'adm') {
  if (!window.n8nChatInitialized) {
    window.n8nChatInitialized = true;

    // 1) Injeta o CSS do n8n.chat
    const chatCss = document.createElement('link');
    chatCss.rel = 'stylesheet';
    chatCss.href = 'https://cdn.jsdelivr.net/npm/@n8n/chat/dist/style.css';
    document.head.appendChild(chatCss);

    // 1.1) Injeta as variáveis de estilo customizadas
    const customStyle = document.createElement('style');
    customStyle.innerHTML = `
      :root {
        --chat--header--background: #e74266;
        --chat--header--color: #ffffff;
        --chat--message--bot--background: #ffffff;
        --chat--message--bot--color: #101330;
        --chat--message--user--background: #e74266;
        --chat--message--user--color: #ffffff;
        --chat--toggle--background: #e74266;
        --chat--toggle--hover--background: #db4061;
        --chat--toggle--active--background: #cf3c5c;
      }
    `;
    document.head.appendChild(customStyle);

    setTimeout(async () => {
      try {
        const { createChat } = await import('https://cdn.jsdelivr.net/npm/@n8n/chat/dist/chat.bundle.es.js');

        const basicAuth = 'Basic YXBwdHJpeDphcHB0cml4QDIwMjU=';

        createChat({
          target: '#n8n-chat',
          webhookUrl: 'https://n8n.mendescorporation.cloud/webhook/1c2a183e-e83c-44d7-98d0-0c3348813c4e/chat',
          webhookConfig: {
            method: 'POST',
            headers: {
              'Authorization': basicAuth,
              'Content-Type': 'application/json'
            }
          },
          mode: 'window',
          loadPreviousSession: false,
          showWelcomeScreen: true,
          allowFileUploads: true,
          defaultLanguage: 'pt',
          initialMessages: [
            'Olá! Qual proposta comercial iremos realizar hoje? Me diz, por favor, o nome do cliente e os detalhes do projeto.'
          ],
          i18n: {
            pt: {
              title: 'Lailatrix',
              subtitle: 'Estou aqui para te ajudar no que precisar.',
              footer: '',
              getStarted: 'Nova Conversa',
              inputPlaceholder: 'Digite sua mensagem...'
            }
          }
        });
      } catch (err) {
        console.error('Erro ao carregar o n8n.chat:', err);
      }
    }, 100);
  }
}

