  if (confirm(`Tem certeza que deseja excluir o projeto ${event.row.name}?`)) {
    const projects = [...variables['b12ebb70-039f-4ccb-a074-9212166ba7ef']];
    const index = projects.findIndex(p => p.id === event.row.id);
    if (index !== -1) {
