const PLUG_USER = pluginVariables[/* Supabase Auth user */ '1fa0dd68-5069-436c-9a7d-3b54c340f1fa']['user']?.['id'];
const COL_USERS = 'e6cc5d40-678e-4b20-9877-25e851b027e2';
const COL_EPICS = '4305da24-0102-4c8c-a58a-cb498639fc2c';
const COL_STORIES = 'a24d55c6-bd49-4326-9803-af5bb8ce7113';

// ===== Projeto =====
const proj = variables?.[VAR_PROJ] ?? {};
const projectId = proj.id ?? null;
const projectName = proj.nome ?? 'Projeto sem nome';
const description = proj.descricao ?? '';

// ===== Normaliza coleção em array (CORRIGIDO) =====
function toArray(col) {
  if (!col) return [];
  if (col.data && Array.isArray(col.data)) return col.data;
  return Array.isArray(col) ? col : Object.values(col);
}

const usuarios = toArray(collections?.[COL_USERS]);
const epicsAll = toArray(collections?.[COL_EPICS]);
const storiesAll = toArray(collections?.[COL_STORIES]);

// ===== Usuário logado (já é o id) =====
if (!PLUG_USER) {
  throw new Error("Não foi possível obter o id do usuário logado (PLUG_USER).");
}

const usuarioLogado = usuarios.find(u => String(u.id) === String(PLUG_USER));
if (!usuarioLogado) {
  throw new Error(`Usuário com id ${PLUG_USER} não encontrado na coleção usuarios`);
}
const responsible = usuarioLogado.nome;

// ===== Filtra épicos do projeto =====
const epicsRaw = projectId
  ? epicsAll.filter(e => String(e.projeto_id) === String(projectId))
  : epicsAll;

// ===== Normaliza épicos =====
const epics = epicsRaw.map(e => ({
  _id: e.id,
  code: e.codigo,
  name: e.nome,
  description: e.descricao,
  stories: []
}));

const epicById = new Map(epics.map(e => [String(e._id), e]));

// ===== Histórias por epico_id =====
storiesAll.forEach(h => {
  const bucket = epicById.get(String(h.epico_id));
  if (!bucket) return;
  bucket.stories.push({
    code: h.codigo,
    name: h.nome,
    description: h.descricao
  });
});

// ===== Ordena épicos por código (ASC; alfa-numérico, vazios por último) =====
epics.sort((a, b) => {
  const ac = a.code ?? '';
  const bc = b.code ?? '';
  if (ac === '' && bc !== '') return 1;
  if (bc === '' && ac !== '') return -1;
  return String(ac).localeCompare(String(bc), undefined, { numeric: true, sensitivity: 'base' });
});

// ===== Payload final =====
const payload = {
  projectName,
  responsible,
  description,
  epics: epics.map(({ _id, ...rest }) => rest)
};

return payload;

