// helper p/ aceitar number ou string "1.234,56"
const toNumber = (v) => {
    if (typeof v === 'number') return v;
    if (typeof v === 'string') {
        const n = parseFloat(v.replace(/\./g, '').replace(',', '.'));
        return isNaN(n) ? 0 : n;
    }
    return 0;
};

const faturas = collections['d05c0288-49e9-4ded-887f-d40744377f69']?.['data'] ?? [];
const contratoId = variables['478ce675-5d37-434f-80b4-48ef92628547']?.['id'];
const valorTotal = variables['478ce675-5d37-434f-80b4-48ef92628547']?.['valor_total'];

if (contratoId == null) return 0;

// filtra faturas do contrato
const faturasDoContrato = faturas.filter(f => String(f?.['contrato_id']) === String(contratoId));

// soma pagas
const somaPagas = faturasDoContrato.reduce((acc, f) => {
    const status = String(f?.['status'] ?? '').toLowerCase();
    return acc + (status === 'pago' ? toNumber(f?.['valor']) : 0);
}, 0);

// base de cálculo: valor_total do contrato, senão soma de todas as faturas
const base = toNumber(valorTotal) || faturasDoContrato.reduce((acc, f) => acc + toNumber(f?.['valor']), 0);

// porcentagem (0–100)
